import React from 'react';
import {
  ChakraProvider,
  Box,
  Input,
  Heading,
  Text,
  extendTheme,
  Flex,
  Button,
  Spacer,
  Center
} from '@chakra-ui/react';
import "@fontsource/poppins/400.css"

function App() {
  //BG #282A37 CMPNT #01CC90
  const config = {
    styles: {
      global: () => ({
        body: {
          bg: "linear-gradient(90deg, rgba(40,43,56,1) 0%, rgba(51,55,72,1) 77%)",
          color: "black",
        },
        fonts: {
          heading: "Poppins",
          body: "Poppins",
        }
      }),
    },
  }

  const theme = extendTheme(config);

  return (
    <ChakraProvider theme={theme}>
      <Heading color="#FFFFFF" fontSize="5xl" fontFamily="poppins" textAlign="center" mt={10} mb={5} minW="100vw"> 
        Smart <span style={{ color: "#01CC90" }}>Image</span> Compressor
      </Heading>
      <Text color="#FFFFFF" fontFamily="poppins" textAlign="center">
        An ultimate image optimizer to compress your images
      </Text>
      <Box mt ={10}>
        <Center>
          <Button color="#01CC90" borderStyle="dotted" borderWidth={5} borderColor="#01CC90" width="30vw" height="15vh">
            test test test
          </Button>
        </Center>
      </Box>
      <Box>
        <Text>

        </Text>
      </Box>

      <Box>
        <Flex>
          <Spacer />
          <Box>
            <Button color="#01CC90" variant="outline">
              test test test
            </Button>
          </Box>
          <Spacer />
          <Box>
            testdfgsdgsdflgskldghsdfkljghsdfgklsdfbklnsdfklbnsdfkbjnsdflbnj
          </Box>
          <Spacer />
        </Flex>
      </Box>
    </ChakraProvider>
  );
}

export default App;
